// Nicholas Buchanan
// January 2, 2010
// PlayerNameSubstrings.java

public class PlayerNameSubstrings {
    public static void main(String[] args) {
        // Original Full Names
        String player1 = "Clinton Portis";
        String player2 = "Jason Campbell";
        String player3 = "Chris Cooley";

        System.out.println("--- NFL Player Name Extractor ---");
        System.out.println("Demonstrating Java's substring() method\n");

        processAndPrint(player1);
        processAndPrint(player2);
        processAndPrint(player3);
    }

    private static void processAndPrint(String fullName) {
        int spaceIndex = fullName.indexOf(" ");

        if(spaceIndex != -1) {
            String firstName = fullName.substring(0, spaceIndex);
            String lastName = fullName.substring(spaceIndex + 1);

            System.out.println("Full Name:  " + fullName);
            System.out.println("First Name: " + firstName);
            System.out.println("Last Name:  " + lastName);
            System.out.println("-----------------------------");
        } else {
            System.out.println("Could not parse: " + fullName);
        }
    }
}
